  var debug = false;

  function init() {
    require(['jquery', 'bootstrap'], function ($, bootstrap) {
      var reference = XWiki.Model.resolve('XWiki.PagePreviewer', XWiki.EntityType.DOCUMENT);
      var serviceUrl = (new XWiki.Document(reference)).getURL();
      var settings = {
        trigger: 'hover',
        placement: 'auto bottom',
        container: 'body',
        delay: { 'show': 500, 'hide': 1000 },
        html: true,
        // selector: ''.wikigeneratedlinkcontent'',
        title: function()
        {
          return $(this).text();
        },
        content: function() {
          if (inProgress)
            return "";
          inProgress = true;
          if (debug) 
            console.log("Launching page preview request");
          // Create a random temporary id for the content's parent div
          // with a unique number just in case.
          var contentId = 'content-' + $.now();
          var imageId = 'image-' + $.now();
          var path = $(this).attr('href');
          if (!path || path=="")
             path = $(this).parent().attr('href');
          if (debug)
           console.log("Path: " + path)
          if (path) {
          $.ajax({
            type: 'GET',
            url: serviceUrl + '?xpage=plain&outputSyntax=plain&path=' + encodeURIComponent(path),
            cache: false,
          }).done( function(data) {
            if (debug)
              console.log("Got result from page preview");
            var linkedPageContent = $('<div>' + data.html + '</div>');
            if (debug)
               console.log(data.html);
            var blocks = linkedPageContent.children(validTags);
            var content = '';
            if (debug)
               console.log(blocks)
            if (blocks.size() > 0) {
               for (var i=0;i<blocks.size();i++) {
                 if (i>=maxBlocks)
                    break;
                 content += blocks[i].outerHTML;
                 if (debug)
                   console.log("Found " + blocks[i].outerHTML)
               }
              if (debug)
                console.log("Found " + i + " blocks")
            }
            // stripping tags
            var allowed = '<' + validTags.replace(/,/g,"><") + '>'
            if (thumbnail)
               allowed = allowed.replace("<img>", "")
            if (debug)
               console.log(allowed)
            content = strip_tags(content, allowed);
            if (debug)
               console.log(content)

            var imageUrl = data.image;
            if (debug)
               console.log(imageUrl)
            var marginTop = '0';
            if (imageUrl && imageUrl.length > 0) {
              $('#' + imageId).html('<div class="col-md-12" style="text-align:center"><img src="' + imageUrl + '" /></div>');
              marginTop = '1em';
            }
            $('#' + contentId).html('<div class="col-md-12" style="margin-top:' + marginTop + '">' + content + '</div>');
            inProgress = false;
            if (debug) {
              console.log("Final Data ready");
              console.log(content);
            }
          });

          var output = '';
          output += '<div class="row" id="' + imageId + '">';
          output +=   '<div class="col-md-12"></div>';
          output += '</div>';
          output += '<div class="row" id="' + contentId + '">';
          output +=   '<div class="col-md-12"><span class="fa fa-spinner fa-pulse"/></div>';
          output += '</div>';
          if (debug)
            console.log("Popup active");
          return output;
         }
          return null;
        }
      }

      // Enable popovers only for not touch-only browsers
      var regex = /Android|iPhone|iPad|Opera Mini/i;
      var match = navigator.userAgent.match(regex);
      if (match == null || match.length == 0) {
       if (activate) {
        if (previewWikiLinks)
         $('#xwikicontent .wikilink a').popover(settings);
        else if (previewGlossary)
         $('.wikigeneratedlinkcontent').popover(settings);

        if (previewMenu)
         $('.menu .wikilink a').popover(settings);

        if (previewSearch)
        $('.search-result-title a').popover(settings);
       }
      }
    });
  }
  (XWiki.domIsLoaded && init()) || document.observe('xwiki:dom:loaded', init);
  return XWiki;
}(XWiki || {}));

